mapper.update:
update tb_user
set password = #{user_pw},
	modified_dt = now(),
	modified_id = #{login_id}
where id = #{user_id}

mapper.insert:
insert into tb_user (
	id, password, name, user_role, remark, created_id, created_dt, modified_id, modified_dt, user_status, phone_number
)
values (
	#{id}, #{encoded_password}, #{name}, #{user_role}, #{remark}, #{login_id}, now(), #{login_id}, now(), #{user_status}, #{phone_number}
)

mapper.select:
SELECT count(id)
FROM tb_user
WHERE 	id = #{user_id}
