mapper.select:
SELECT count(id)
FROM tb_user
WHERE 	id = #{id}

SELECT tu.id, tu.user_status, tbm.branch_code as branch_code_col, tbm.branch_name, join_code, to_char(tbi.created_dt,'YYYY-MM-DD') as created_dt, tbi.branch_tel, tbi.branch_addr, tbi.business_hours, std_due_date FROM tb_branch_mng tbm join tb_branch_info tbi on tbm.branch_code = tbi.branch_code join tb_user tu on tbm.branch_code = tu.branch_code and tu.user_role = 'ADMIN'
tbm.branch_code != 'b0000' and tbm.branch_name like '%'|| #{branchName} ||'%' and tu.id like '%'|| #{adminId} ||'%' and join_code like '%'|| #{joinCode} ||'%' and user_status like '%'|| #{userStatus} ||'%'
ORDER BY tbm.branch_code;

mapper.insert:
