mapper.select:
SELECT a.branch_code, a.rsv_month, a.rsv_date, a."1t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't1') AS "1t_cnt", a."2t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't2') AS "2t_cnt", a."3t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't3') AS "3t_cnt", a."4t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't4') AS "4t_cnt", a."5t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't5') AS "5t_cnt", a."6t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't6') AS "6t_cnt", a."7t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't7') AS "7t_cnt", a."8t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't8') AS "8t_cnt", a."9t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't9') AS "9t_cnt", a."10t", COUNT(b.select_time) FILTER (WHERE b.select_time = 't10') AS "10t_cnt" FROM tb_reservation_master a LEFT OUTER JOIN tb_reservation_list b on a.branch_code = b.branch_code AND a.rsv_month = b.rsv_month AND a.rsv_date = b.rsv_date
a.branch_code = #{branch_code} and a.rsv_month = #{rsv_month}
a.branch_code = #{super_branch_code} and a.rsv_month = #{rsv_month}
GROUP BY a.branch_code, a.rsv_month, a.rsv_date ORDER BY a.rsv_month, a.rsv_date

SELECT count(branch_code)
FROM tb_reservation_info

	rsv_month = #{rsv_month}


			and branch_code = #{branch_code}


			and branch_code = #{super_branch_code}

SELECT t1_name, t2_name, t3_name, t4_name, t5_name, t6_name, t7_name, t8_name, t9_name, t10_name
FROM tb_reservation_info



			AND branch_code = #{branch_code}


			AND branch_code = #{super_branch_code}



ORDER BY rsv_month desc

SELECT a.rsv_date, COALESCE(reservation_cnt, 0) as reservation_cnt, ("1t" + "2t" + "3t" + "4t" + "5t" + "6t" + "7t" + "8t" + "9t" + "10t") as "open_rsv_cnt"
from tb_reservation_master a
LEFT JOIN (
			select rsv_date, count(rsv_date) reservation_cnt
			from tb_reservation_list

				rsv_month like #{rsv_month}


						and branch_code = #{branch_code}


						and branch_code = #{super_branch_code}



			group by rsv_date
) b ON a.rsv_date = b.rsv_date

	rsv_month like #{rsv_month}


			and branch_code = #{branch_code}


			and branch_code = #{super_branch_code}

SELECT rsv_date, a.user_id, c.phone_number, c.name as user_name, CASE WHEN a.select_time = 't1' THEN b."t1_name" WHEN a.select_time = 't2' THEN b."t2_name" WHEN a.select_time = 't3' THEN b."t3_name" WHEN a.select_time = 't4' THEN b."t4_name" WHEN a.select_time = 't5' THEN b."t5_name" WHEN a.select_time = 't6' THEN b."t6_name" WHEN a.select_time = 't7' THEN b."t7_name" WHEN a.select_time = 't8' THEN b."t8_name" WHEN a.select_time = 't9' THEN b."t9_name" WHEN a.select_time = 't10' THEN b."t10_name" END AS "reservation_time" FROM tb_reservation_list a JOIN tb_reservation_info b ON a.branch_code = b.branch_code JOIN tb_user c on a.user_id = c.id
a.rsv_date = #{rsv_date}
and a.branch_code = #{branch_code}
and a.branch_code = #{super_branch_code}

SELECT a.branch_name, a.branch_tel, a.branch_addr, a.business_hours, b.join_code
FROM tb_branch_info a
LEFT OUTER JOIN tb_branch_mng b on a.branch_code = b.branch_code 



			and a.branch_code = #{branch_code}


			and a.branch_code = #{super_branch_code}

select.foreach:
SELECT
	't${timeSlot}' AS col,
	(	SELECT "${timeSlot}t"
		FROM tb_reservation_master 

			rsv_month = #{rsv_month} AND rsv_date = #{rsv_date}


					and branch_code = #{branch_code}


					and branch_code = #{super_branch_code}



	)
	-
	(	SELECT COUNT(*)
		FROM tb_reservation_list

			rsv_month = #{rsv_month} AND rsv_date = #{rsv_date} AND select_time = 't${timeSlot}'


					and branch_code = #{branch_code}


					and branch_code = #{super_branch_code}



	) AS cnt,
	(	SELECT "t${timeSlot}_name"
		FROM tb_reservation_info tri 



					and branch_code = #{branch_code}


					and branch_code = #{super_branch_code}



	) as rsv_time

mapper.insert:
INSERT into tb_reservation_list (
	rsv_month, rsv_date, select_time, user_id, created_id, created_dt, modified_id, modified_dt, rsv_status, branch_code
)
VALUES (
	#{rsv_month}, #{rsv_date}, #{select_time}, #{user_id}, #{login_id}, now(), #{login_id}, now(), 'N',


			#{branch_code}


			#{super_branch_code}


)

INSERT INTO tb_cancel_log(
	rsv_date, select_time, created_id, created_dt, reservation_id, branch_code
)
VALUES(
	#{rsv_date}, #{select_time}, #{login_id}, now(), #{user_id}, 


			#{branch_code}


			#{super_branch_code}


)

mapper.update:
UPDATE tb_reservation_list
SET rsv_status = #{rsv_status}
WHERE user_id = #{user_id} and select_time = #{select_time} and rsv_date = #{rsv_date}

mapper.select:
SELECT rsv_month, created_dt FROM tb_reservation_master
and branch_code = #{branch_code}
and branch_code = #{super_branch_code}
GROUP BY rsv_month, branch_code, rsv_date, created_dt ORDER BY rsv_month desc, rsv_date desc LIMIT 1

SELECT a.id, a.name, a.phone_number, TO_CHAR(a.created_dt, 'YYYY-MM-DD') as created_dt, CASE WHEN b.has_reservation = a.massage_total and massage_cnt = 0 THEN 'Y' ELSE 'N' END AS reserve_cnt_status, CASE WHEN c.status IS NOT NULL THEN c.status ELSE 'N' END AS msg_status FROM tb_user a LEFT OUTER JOIN ( SELECT user_id, branch_code, COUNT(*) AS has_reservation FROM tb_reservation_list GROUP BY user_id, branch_code ) b ON a.branch_code = b.branch_code AND a.id = b.user_id LEFT OUTER JOIN ( SELECT user_id, status FROM tb_msg_log WHERE msg_type = 'join' ) c ON a.id = c.user_id
DATE(a.created_dt) >= CURRENT_DATE - INTERVAL '6 day'
and a.branch_code = #{branch_code}
and a.branch_code = #{super_branch_code}
ORDER BY a.created_dt DESC

SELECT a.user_id, a.rsv_date, a.select_time, TO_CHAR(a.created_dt, 'YYYY-MM-DD') as created_dt, CASE WHEN a.select_time = 't1' then b.t1_name WHEN a.select_time = 't2' then b.t2_name WHEN a.select_time = 't3' then b.t3_name WHEN a.select_time = 't4' then b.t4_name WHEN a.select_time = 't5' then b.t5_name WHEN a.select_time = 't6' then b.t6_name WHEN a.select_time = 't7' then b.t7_name WHEN a.select_time = 't8' then b.t8_name WHEN a.select_time = 't9' then b.t9_name WHEN a.select_time = 't10' then b.t10_name END as select_time_nm, CASE WHEN c.status IS NOT NULL THEN c.status ELSE 'N' END AS msg_status FROM tb_reservation_list a LEFT OUTER JOIN tb_reservation_info b on b.branch_code = a.branch_code LEFT OUTER JOIN ( SELECT user_id, status, rsv_date FROM tb_msg_log WHERE msg_type = 'rsv' ) c ON a.user_id = c.user_id and a.rsv_date = c.rsv_date
DATE(a.created_dt) >= CURRENT_DATE - INTERVAL '6 day'
and a.branch_code = #{branch_code}
and a.branch_code = #{super_branch_code}
ORDER BY a.user_id, created_dt desc

SELECT a.id, a.phone_number, a.massage_total, a.massage_cnt, a.massage_total - a.massage_cnt as free_rsv_cnt, a.due_date, TO_CHAR(TO_DATE(a.due_date, 'YYYY-MM-DD') - INTERVAL '280 days', 'YYYY-MM-DD') AS last_menstrual_date, TRUNC((CURRENT_DATE - To_char(TO_DATE(a.due_date, 'YYYY-MM-DD') - INTERVAL '280 days', 'YYYY-MM-DD')::date)::numeric / 7) AS pregnancy_weeks, (CURRENT_DATE - To_char(TO_DATE(a.due_date, 'YYYY-MM-DD') - INTERVAL '280 days', 'YYYY-MM-DD')::date)::numeric % 7 AS pregnancy_day, CASE WHEN b.status IS NOT NULL THEN b.status ELSE 'N' END AS msg_status FROM tb_user a LEFT OUTER JOIN ( SELECT user_id, status FROM tb_msg_log WHERE msg_type = 'non_rsv' ) b ON a.id = b.user_id
a.user_role = 'USER' AND a.user_status = 'Y' AND a.massage_total - a.massage_cnt > 0
and a.branch_code = #{branch_code}
and a.branch_code = #{super_branch_code}
ORDER BY a.created_dt desc

SELECT count(*)
FROM tb_msg_log
WHERE user_id = #{user_id} 
		and branch_code = #{branch_code} 
		and msg_type = #{msg_type}


				AND rsv_date = #{rsv_date}

mapper.update:
UPDATE tb_msg_log
